mapper.select:
		
		SELECT  notice_id, notice_title, notice_name 
		FROM
        		( SELECT ROWNUM rn, a.*
        		  FROM
                            (SELECT  * 
                             FROM    notice
                             WHERE   n_delete = 0
                             ) a
        		)
		WHERE rn >=  #{start}
		AND   rn <=  #{end}

SELECT notice_id, notice_title, notice_name FROM ( SELECT ROWNUM rn, a.* FROM (
SELECT notice_id, notice_title, notice_name FROM notice WHERE n_delete = 0 AND notice_title LIKE '%' || #{keyword} || '%' ) a
SELECT notice_id, notice_title, notice_name FROM notice WHERE n_delete = 0 AND notice_name LIKE '%' || #{keyword} || '%' ) a
) WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE  notice
SET     n_delete = 1
WHERE   notice_id = #{notice_id}
